script.code:

// Query a LDAP server requires authentication for connection
// Please look at the following link for the configuration LDAP credentials:
// http://doc.activeeon.com/latest/admin/ProActiveAdminGuide.html#_ldap_query

// Please fill variables
ldapUrl = "ldap://localhost:389"
ldapSearchBase = "dc=yourOrganization,dc=com"
ldapSearchFilter = "(objectclass=*)"
ldapSelectedAttributes = "attributeName1,attributeName2"

// Don't change code below
variables.put("ldapUrl", ldapUrl);
variables.put("ldapSearchBase", ldapSearchBase);
variables.put("ldapSearchFilter", ldapSearchFilter);
variables.put("ldapSelectedAttributes", ldapSelectedAttributes);

import org.ow2.proactive.addons.ldap_query.LDAPClient;
LDAPClient ldapClient  = new LDAPClient(variables, credentials);
String ldapResult = ldapClient.searchQueryLDAP();
result = ldapResult;

System.out.println(result);
